import 'package:flutter/material.dart';\nimport '../models/hotel.dart';\n\nclass HotelDetailsScreen extends StatelessWidget {\n  const HotelDetailsScreen({super.key});\n\n  @override\n  Widget build(BuildContext context) {\n    final Hotel hotel = ModalRoute.of(context)!.settings.arguments as Hotel;\n\n    return Scaffold(\n      body: CustomScrollView(\n        slivers: [\n          // App bar with image\n          SliverAppBar(\n            expandedHeight: 300,\n            pinned: true,\n            flexibleSpace: FlexibleSpaceBar(\n              title: Text(\n                hotel.name,\n                style: const TextStyle(\n                  color: Colors.white,\n                  fontWeight: FontWeight.bold,\n                  shadows: [\n                    Shadow(\n                      offset: Offset(0, 1),\n                      blurRadius: 3,\n                      color: Colors.black45,\n                    ),\n                  ],\n                ),\n              ),\n              background: Stack(\n                fit: StackFit.expand,\n                children: [\n                  Image.network(\n                    hotel.images.first,\n                    fit: BoxFit.cover,\n                    errorBuilder: (context, error, stackTrace) {\n                      return Container(\n                        color: Colors.grey[300],\n                        child: const Icon(Icons.hotel, size: 64),\n                      );\n                    },\n                  ),\n                  Container(\n                    decoration: BoxDecoration(\n                      gradient: LinearGradient(\n                        begin: Alignment.topCenter,\n                        end: Alignment.bottomCenter,\n                        colors: [\n                          Colors.transparent,\n                          Colors.black.withOpacity(0.7),\n                        ],\n                      ),\n                    ),\n                  ),\n                ],\n              ),\n            ),\n          ),\n          // Content\n          SliverToBoxAdapter(\n            child: Padding(\n              padding: const EdgeInsets.all(16),\n              child: Column(\n                crossAxisAlignment: CrossAxisAlignment.start,\n                children: [\n                  // Location and rating\n                  Row(\n                    children: [\n                      const Icon(Icons.location_on, color: Colors.red, size: 20),\n                      const SizedBox(width: 4),\n                      Expanded(\n                        child: Text(\n                          hotel.location,\n                          style: const TextStyle(fontSize: 16),\n                        ),\n                      ),\n                      const Icon(Icons.star, color: Colors.amber, size: 20),\n                      const SizedBox(width: 4),\n                      Text(\n                        hotel.rating.toString(),\n                        style: const TextStyle(\n                          fontSize: 16,\n                          fontWeight: FontWeight.bold,\n                        ),\n                      ),\n                    ],\n                  ),\n                  const SizedBox(height: 24),\n                  // Price\n                  Container(\n                    padding: const EdgeInsets.all(16),\n                    decoration: BoxDecoration(\n                      color: Theme.of(context).colorScheme.primaryContainer,\n                      borderRadius: BorderRadius.circular(12),\n                    ),\n                    child: Row(\n                      mainAxisAlignment: MainAxisAlignment.spaceBetween,\n                      children: [\n                        const Text(\n                          'Price per night',\n                          style: TextStyle(fontSize: 16),\n                        ),\n                        Text(\n                          '\$${hotel.pricePerNight.toStringAsFixed(2)}',\n                          style: TextStyle(\n                            fontSize: 24,\n                            fontWeight: FontWeight.bold,\n                            color: Theme.of(context).colorScheme.primary,\n                          ),\n                        ),\n                      ],\n                    ),\n                  ),\n                  const SizedBox(height: 24),\n                  // Description\n                  const Text(\n                    'About this hotel',\n                    style: TextStyle(\n                      fontSize: 20,\n                      fontWeight: FontWeight.bold,\n                    ),\n                  ),\n                  const SizedBox(height: 8),\n                  Text(\n                    hotel.description,\n                    style: TextStyle(\n                      fontSize: 16,\n                      color: Colors.grey[700],\n                      height: 1.5,\n                    ),\n                  ),\n                  const SizedBox(height: 24),\n                  // Amenities\n                  const Text(\n                    'Amenities',\n                    style: TextStyle(\n                      fontSize: 20,\n                      fontWeight: FontWeight.bold,\n                    ),\n                  ),\n                  const SizedBox(height: 12),\n                  Wrap(\n                    spacing: 8,\n                    runSpacing: 8,\n                    children: hotel.amenities.map((amenity) {\n                      return Chip(\n                        label: Text(amenity),\n                        avatar: Icon(\n                          _getAmenityIcon(amenity),\n                          size: 18,\n                        ),\n                        backgroundColor: Theme.of(context)\n                            .colorScheme\n                            .secondaryContainer,\n                      );\n                    }).toList(),\n                  ),\n                  const SizedBox(height: 24),\n                  // Room info\n                  Container(\n                    padding: const EdgeInsets.all(16),\n                    decoration: BoxDecoration(\n                      border: Border.all(color: Colors.grey[300]!),\n                      borderRadius: BorderRadius.circular(12),\n                    ),\n                    child: Row(\n                      children: [\n                        const Icon(Icons.meeting_room, size: 32),\n                        const SizedBox(width: 12),\n                        Text(\n                          '${hotel.totalRooms} rooms available',\n                          style: const TextStyle(fontSize: 16),\n                        ),\n                      ],\n                    ),\n                  ),\n                  const SizedBox(height: 100),\n                ],\n              ),\n            ),\n          ),\n        ],\n      ),\n      // Book now button\n      bottomSheet: Container(\n        padding: const EdgeInsets.all(16),\n        decoration: BoxDecoration(\n          color: Colors.white,\n          boxShadow: [\n            BoxShadow(\n              color: Colors.black.withOpacity(0.1),\n              blurRadius: 10,\n              offset: const Offset(0, -2),\n            ),\n          ],\n        ),\n        child: SafeArea(\n          child: SizedBox(\n            width: double.infinity,\n            child: ElevatedButton(\n              onPressed: () {\n                Navigator.pushNamed(\n                  context,\n                  '/booking',\n                  arguments: hotel,\n                );\n              },\n              style: ElevatedButton.styleFrom(\n                backgroundColor: Theme.of(context).colorScheme.primary,\n                foregroundColor: Colors.white,\n                padding: const EdgeInsets.symmetric(vertical: 16),\n                shape: RoundedRectangleBorder(\n                  borderRadius: BorderRadius.circular(12),\n                ),\n              ),\n              child: const Text(\n                'Book Now',\n                style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold),\n              ),\n            ),\n          ),\n        ),\n      ),\n    );\n  }\n\n  IconData _getAmenityIcon(String amenity) {\n    switch (amenity.toLowerCase()) {\n      case 'wifi':\n        return Icons.wifi;\n      case 'pool':\n        return Icons.pool;\n      case 'gym':\n        return Icons.fitness_center;\n      case 'restaurant':\n        return Icons.restaurant;\n      case 'spa':\n        return Icons.spa;\n      case 'room service':\n        return Icons.room_service;\n      case 'parking':\n        return Icons.local_parking;\n      case 'bar':\n        return Icons.local_bar;\n      case 'beach access':\n      case 'beach':\n        return Icons.beach_access;\n      case 'business center':\n        return Icons.business_center;\n      case 'hot tub':\n        return Icons.hot_tub;\n      case 'golf course':\n        return Icons.golf_course;\n      case 'fine dining':\n        return Icons.dinner_dining;\n      default:\n        return Icons.check_circle;\n    }\n  }\n}\n