import '../models/hotel.dart';\nimport '../models/booking.dart';\n\nclass HotelService {\n  // Mock data service - replace with actual API calls when Supabase is connected\n  \n  static List<Hotel> _mockHotels = [\n    Hotel(\n      id: '1',\n      name: 'Grand Plaza Hotel',\n      location: 'New York, USA',\n      description: 'Luxury hotel in the heart of Manhattan with stunning city views. Experience world-class service and amenities.',\n      rating: 4.8,\n      pricePerNight: 299.99,\n      amenities: ['WiFi', 'Pool', 'Gym', 'Restaurant', 'Spa', 'Room Service'],\n      images: ['https://images.unsplash.com/photo-1566073771259-6a8506099945?w=800'],\n      totalRooms: 150,\n    ),\n    Hotel(\n      id: '2',\n      name: 'Seaside Resort',\n      location: 'Miami, Florida',\n      description: 'Beachfront paradise with private beach access and ocean views from every room.',\n      rating: 4.6,\n      pricePerNight: 249.99,\n      amenities: ['WiFi', 'Beach Access', 'Pool', 'Bar', 'Water Sports'],\n      images: ['https://images.unsplash.com/photo-1520250497591-112f2f40a3f4?w=800'],\n      totalRooms: 100,\n    ),\n    Hotel(\n      id: '3',\n      name: 'Mountain View Lodge',\n      location: 'Aspen, Colorado',\n      description: 'Cozy mountain retreat perfect for ski enthusiasts and nature lovers.',\n      rating: 4.7,\n      pricePerNight: 199.99,\n      amenities: ['WiFi', 'Fireplace', 'Ski Storage', 'Restaurant', 'Hot Tub'],\n      images: ['https://images.unsplash.com/photo-1542314831-068cd1dbfeeb?w=800'],\n      totalRooms: 80,\n    ),\n    Hotel(\n      id: '4',\n      name: 'City Center Hotel',\n      location: 'San Francisco, CA',\n      description: 'Modern hotel in downtown with easy access to major attractions and business districts.',\n      rating: 4.5,\n      pricePerNight: 179.99,\n      amenities: ['WiFi', 'Business Center', 'Gym', 'Parking', 'Restaurant'],\n      images: ['https://images.unsplash.com/photo-1551882547-ff40c63fe5fa?w=800'],\n      totalRooms: 120,\n    ),\n    Hotel(\n      id: '5',\n      name: 'Tropical Paradise Resort',\n      location: 'Honolulu, Hawaii',\n      description: 'Island luxury with pristine beaches, tropical gardens, and authentic Hawaiian hospitality.',\n      rating: 4.9,\n      pricePerNight: 399.99,\n      amenities: ['WiFi', 'Beach', 'Multiple Pools', 'Spa', 'Golf Course', 'Fine Dining'],\n      images: ['https://images.unsplash.com/photo-1571003123894-1f0594d2b5d9?w=800'],\n      totalRooms: 200,\n    ),\n  ];\n\n  static List<Booking> _mockBookings = [];\n\n  // Get all hotels\n  Future<List<Hotel>> getAllHotels() async {\n    // Simulate network delay\n    await Future.delayed(const Duration(milliseconds: 500));\n    return List.from(_mockHotels);\n  }\n\n  // Search hotels by location\n  Future<List<Hotel>> searchHotels(String query) async {\n    await Future.delayed(const Duration(milliseconds: 300));\n    if (query.isEmpty) return getAllHotels();\n    \n    return _mockHotels.where((hotel) {\n      return hotel.name.toLowerCase().contains(query.toLowerCase()) ||\n             hotel.location.toLowerCase().contains(query.toLowerCase());\n    }).toList();\n  }\n\n  // Get hotel by ID\n  Future<Hotel?> getHotelById(String id) async {\n    await Future.delayed(const Duration(milliseconds: 200));\n    try {\n      return _mockHotels.firstWhere((hotel) => hotel.id == id);\n    } catch (e) {\n      return null;\n    }\n  }\n\n  // Create a booking\n  Future<Booking> createBooking({\n    required String hotelId,\n    required DateTime checkInDate,\n    required DateTime checkOutDate,\n    required int numberOfGuests,\n    required int numberOfRooms,\n  }) async {\n    await Future.delayed(const Duration(milliseconds: 500));\n    \n    final hotel = await getHotelById(hotelId);\n    if (hotel == null) throw Exception('Hotel not found');\n\n    final numberOfNights = checkOutDate.difference(checkInDate).inDays;\n    final totalPrice = hotel.pricePerNight * numberOfNights * numberOfRooms;\n\n    final booking = Booking(\n      id: DateTime.now().millisecondsSinceEpoch.toString(),\n      hotelId: hotelId,\n      hotelName: hotel.name,\n      hotelLocation: hotel.location,\n      checkInDate: checkInDate,\n      checkOutDate: checkOutDate,\n      numberOfGuests: numberOfGuests,\n      numberOfRooms: numberOfRooms,\n      totalPrice: totalPrice,\n      status: 'confirmed',\n      bookingDate: DateTime.now(),\n    );\n\n    _mockBookings.add(booking);\n    return booking;\n  }\n\n  // Get all bookings\n  Future<List<Booking>> getAllBookings() async {\n    await Future.delayed(const Duration(milliseconds: 300));\n    return List.from(_mockBookings);\n  }\n\n  // Cancel a booking\n  Future<bool> cancelBooking(String bookingId) async {\n    await Future.delayed(const Duration(milliseconds: 300));\n    final index = _mockBookings.indexWhere((b) => b.id == bookingId);\n    if (index != -1) {\n      final booking = _mockBookings[index];\n      _mockBookings[index] = Booking(\n        id: booking.id,\n        hotelId: booking.hotelId,\n        hotelName: booking.hotelName,\n        hotelLocation: booking.hotelLocation,\n        checkInDate: booking.checkInDate,\n        checkOutDate: booking.checkOutDate,\n        numberOfGuests: booking.numberOfGuests,\n        numberOfRooms: booking.numberOfRooms,\n        totalPrice: booking.totalPrice,\n        status: 'cancelled',\n        bookingDate: booking.bookingDate,\n      );\n      return true;\n    }\n    return false;\n  }\n}\n