import 'package:flutter/material.dart';\nimport '../models/booking.dart';\n\nclass BookingCard extends StatelessWidget {\n  final Booking booking;\n  final VoidCallback onCancel;\n\n  const BookingCard({\n    super.key,\n    required this.booking,\n    required this.onCancel,\n  });\n\n  Color _getStatusColor() {\n    switch (booking.status) {\n      case 'confirmed':\n        return Colors.green;\n      case 'pending':\n        return Colors.orange;\n      case 'cancelled':\n        return Colors.red;\n      default:\n        return Colors.grey;\n    }\n  }\n\n  IconData _getStatusIcon() {\n    switch (booking.status) {\n      case 'confirmed':\n        return Icons.check_circle;\n      case 'pending':\n        return Icons.schedule;\n      case 'cancelled':\n        return Icons.cancel;\n      default:\n        return Icons.info;\n    }\n  }\n\n  @override\n  Widget build(BuildContext context) {\n    return Card(\n      margin: const EdgeInsets.only(bottom: 16),\n      child: Padding(\n        padding: const EdgeInsets.all(16),\n        child: Column(\n          crossAxisAlignment: CrossAxisAlignment.start,\n          children: [\n            // Status badge\n            Row(\n              mainAxisAlignment: MainAxisAlignment.spaceBetween,\n              children: [\n                Container(\n                  padding: const EdgeInsets.symmetric(\n                    horizontal: 12,\n                    vertical: 6,\n                  ),\n                  decoration: BoxDecoration(\n                    color: _getStatusColor().withOpacity(0.1),\n                    borderRadius: BorderRadius.circular(20),\n                  ),\n                  child: Row(\n                    mainAxisSize: MainAxisSize.min,\n                    children: [\n                      Icon(\n                        _getStatusIcon(),\n                        size: 16,\n                        color: _getStatusColor(),\n                      ),\n                      const SizedBox(width: 6),\n                      Text(\n                        booking.status.toUpperCase(),\n                        style: TextStyle(\n                          color: _getStatusColor(),\n                          fontWeight: FontWeight.bold,\n                          fontSize: 12,\n                        ),\n                      ),\n                    ],\n                  ),\n                ),\n                Text(\n                  'ID: ${booking.id}',\n                  style: TextStyle(\n                    color: Colors.grey[600],\n                    fontSize: 12,\n                  ),\n                ),\n              ],\n            ),\n            const SizedBox(height: 16),\n            // Hotel info\n            Text(\n              booking.hotelName,\n              style: const TextStyle(\n                fontSize: 18,\n                fontWeight: FontWeight.bold,\n              ),\n            ),\n            const SizedBox(height: 8),\n            Row(\n              children: [\n                const Icon(Icons.location_on, size: 16, color: Colors.red),\n                const SizedBox(width: 4),\n                Text(\n                  booking.hotelLocation,\n                  style: TextStyle(color: Colors.grey[600]),\n                ),\n              ],\n            ),\n            const SizedBox(height: 16),\n            const Divider(),\n            const SizedBox(height: 16),\n            // Booking details\n            Row(\n              children: [\n                Expanded(\n                  child: Column(\n                    crossAxisAlignment: CrossAxisAlignment.start,\n                    children: [\n                      Text(\n                        'Check-in',\n                        style: TextStyle(\n                          color: Colors.grey[600],\n                          fontSize: 12,\n                        ),\n                      ),\n                      const SizedBox(height: 4),\n                      Text(\n                        '${booking.checkInDate.day}/${booking.checkInDate.month}/${booking.checkInDate.year}',\n                        style: const TextStyle(\n                          fontWeight: FontWeight.bold,\n                        ),\n                      ),\n                    ],\n                  ),\n                ),\n                const Icon(Icons.arrow_forward, size: 20),\n                Expanded(\n                  child: Column(\n                    crossAxisAlignment: CrossAxisAlignment.end,\n                    children: [\n                      Text(\n                        'Check-out',\n                        style: TextStyle(\n                          color: Colors.grey[600],\n                          fontSize: 12,\n                        ),\n                      ),\n                      const SizedBox(height: 4),\n                      Text(\n                        '${booking.checkOutDate.day}/${booking.checkOutDate.month}/${booking.checkOutDate.year}',\n                        style: const TextStyle(\n                          fontWeight: FontWeight.bold,\n                        ),\n                      ),\n                    ],\n                  ),\n                ),\n              ],\n            ),\n            const SizedBox(height: 16),\n            Row(\n              mainAxisAlignment: MainAxisAlignment.spaceBetween,\n              children: [\n                Row(\n                  children: [\n                    const Icon(Icons.nights_stay, size: 16),\n                    const SizedBox(width: 4),\n                    Text('${booking.numberOfNights} nights'),\n                    const SizedBox(width: 16),\n                    const Icon(Icons.person, size: 16),\n                    const SizedBox(width: 4),\n                    Text('${booking.numberOfGuests} guests'),\n                    const SizedBox(width: 16),\n                    const Icon(Icons.meeting_room, size: 16),\n                    const SizedBox(width: 4),\n                    Text('${booking.numberOfRooms} rooms'),\n                  ],\n                ),\n              ],\n            ),\n            const SizedBox(height: 16),\n            const Divider(),\n            const SizedBox(height: 16),\n            // Total price and action button\n            Row(\n              mainAxisAlignment: MainAxisAlignment.spaceBetween,\n              children: [\n                Column(\n                  crossAxisAlignment: CrossAxisAlignment.start,\n                  children: [\n                    Text(\n                      'Total Price',\n                      style: TextStyle(\n                        color: Colors.grey[600],\n                        fontSize: 12,\n                      ),\n                    ),\n                    const SizedBox(height: 4),\n                    Text(\n                      '\$${booking.totalPrice.toStringAsFixed(2)}',\n                      style: TextStyle(\n                        fontSize: 20,\n                        fontWeight: FontWeight.bold,\n                        color: Theme.of(context).colorScheme.primary,\n                      ),\n                    ),\n                  ],\n                ),\n                if (booking.status == 'confirmed')\n                  OutlinedButton.icon(\n                    onPressed: onCancel,\n                    icon: const Icon(Icons.cancel_outlined),\n                    label: const Text('Cancel'),\n                    style: OutlinedButton.styleFrom(\n                      foregroundColor: Colors.red,\n                    ),\n                  ),\n              ],\n            ),\n          ],\n        ),\n      ),\n    );\n  }\n}\n